//         productName: string;
//         isFeatured: boolean;
//         productBrand: string;
//         productRating: number;
//         productImage?: String;
//         productPrice: Number;
//       }[]
//     | undefined
//   >
  (CardItemDetails);
  const handleOnChange=(val:any)=>
  {
      const searchedResult=CardItemDetails.filter(item=>item.productBrand.toLowerCase().includes(val));
      console.log(searchedResult);
      setProductList(searchedResult);
  }
  console.log(CardItemDetails.filter(product=>product.productName.toLowerCase().includes("Ap")));
  return (
    <>
  
<SearchSpace>
            <Input
              type="text"
              placeholder="Search Product"
            //   value={productList}
              onChange={(e) => 
                // handleOnChange(e.target.value)
                setText(e.target.value)
              }
            ></Input>
            <FaSearch />
          </SearchSpace>
          <CardItem>
            {
                CardItemDetails.filter(product=>product.productName.toLowerCase().includes(text)).map((product) => {
                    
                    return (
                      <SearchDisplay
                        
                        id={product?.productId}
                        name={product?.productName}
                        brand={product?.productBrand}
                        price={product?.productPrice}
                        rating={product?.productRating}
                        image={product?.productImage}
                      />
                    );
                  })
            }
          </CardItem>
      
 
  </>
  );
};
