import React from 'react';
import { Input,SearchSpace,SortSpace,FilterSpace, FilterDivison} from "./styles";
import { useState } from "react";

export const CardItems:React.FC=()=> {

const products=[
    {
        name:"Loga",
        age:21,
        designation:"student",
    },
    {
        name:"Jaan",
        age:20,
        designation:"student",
    },
    {
        name:"Maals",
        age:20,
        designation:"student",
    },
    {
        name:"Priya",
        age:23,
        designation:"student",
    },

]
const [productList,setProductList]=React.useState<{name:string,age:number,designation:string}[] | undefined>(products);
const [text,setText]=React.useState<string>('');
const handleOnClick=()=>
{
  const findproducts=
  productList && productList?.length>0 ? productList?.filter((u)=>u?.name===text)
  :undefined;
  setProductList(findproducts);
};
    return (
     <div>
    
        {/* <Input type="text" placeholder="Search" ></Input> */}
        <input type="text"
        placeholder="Search Product"
        value={text}
        onChange={(e)=>{setText(e.target.value);
         setProductList(products);
        }}></input>
        <button disabled={!text} onClick={handleOnClick}>SEARCH</button>
        {/* </SearchSpace> */}
        <div>
            {
                productList && productList?.length===0 && (
                    <div>OopsNo Product Found</div>
                )
            }
            {

                productList && productList?.length>0 && productList?.map(product =>{
                    return(
                   <div>
                    <h3>Name:{product?.name}</h3>
                    <p>Age:{product?.age}</p>
                    <p>Designation:{product?.designation}</p>
                   </div>
                    );
                })
            }
        </div>
     </div>

    );
  }
