import CardItemDetails from "./CardItemDetails";
import React from "react";
import SearchDisplay from "./SearchDisplay";

import {
  Input,
  SearchSpace,
  SortSpace,
  FilterSpace,
  FilterDivison,
  CardItem,
  Button,
} from "./styles";
import { sortSpace, filterSpace, productDetails } from "../../data/String";
import { FaSearch, FaSort, FaFilter } from "react-icons/fa";


export const Search: React.FC = () => {
    const [text,setText]=React.useState<string>("");
    const [productList, setProductList] = React.useState<
    | {
        productId: number;
        productName: string;
        isFeatured: boolean;
        productBrand: string;
        productRating: number;
        productImage?: String;
        productPrice: Number;
      }[]
    | undefined
  >(CardItemDetails);
  const handleOnChange=(val:any)=>
  {
      const searchedResult=CardItemDetails.filter(item=>item.productBrand.toLowerCase().includes(val));
      console.log(searchedResult);
      setProductList(searchedResult);
  }
  console.log(text);
  return (
    <>
  
<SearchSpace>
            <Input
              type="text"
              placeholder="Search Product"
            //   value={productList}
              onChange={(e) => 
                // handleOnChange(e.target.value)
                setText(e.target.value)
              }
            ></Input>
            <FaSearch />
          </SearchSpace>
          <CardItem>
            {
                CardItemDetails?.map((product) => {
                    
                    return (
                      <SearchDisplay
                        
                        key={product?.productId}
                        name={product?.productName}
                        brand={product?.productBrand}
                        price={product?.productPrice}
                        rating={product?.productRating}
                        image={product?.productImage}
                      />
                    );
                  })
            }
          </CardItem>
      
 
  </>
  );
};
