import styled from "styled-components";

//Navigation content

export const Title = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-around;
  padding: 30px;
  margin-bottom:20px;
  color: white;
  font-size: 20px;
  background-color: #4b4b4b;
`;
export const LogoImage = styled.img`
  width: 6vw;
  height: 6vh;
`;
export const AnchorTag = styled.a`
  cursor: pointer;

  &:hover {
    color: black;
    text-shadow: 0 0 3px slategray;
  }
`;
//featured items content

export const Heading = styled.h3`
  color: darkgrey;
  font-size: 25;
  
`;
export const FeaturedBox = styled.div`
  box-shadow: 0 0 3px slategray;
  margin: 10px auto;
  width: 94%;
  padding: 10px 0px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items:flex

`;

//search items content

export const FilterDivison = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const SearchSpace = styled.div`
  width: 20%;
  display: flex;
  justify-content: space-around;
  font-size: 18px;
  padding: 15px;
  margin: 10px;
  background: whitesmoke;
  color: #4b4b4b;
  border-radius: 3px;
`;

export const Input = styled.input`
  background: whitesmoke;
  // width: 20%;
  padding: 0;
  margin: 0;
  color: #4b4b4b;
  outline: none;
  border: none;
  font-size: 18px;
  ::placeholder {
    color: #4b4b4b;
  }
`;

///sort
export const SortSpace = styled(SearchSpace)`
  width: 10%;
`;
export const FilterSpace = styled(SortSpace)``;

//card items

export const CardContainer = styled.div`
  width: 90%;
  box-shadow: 0 0 3px slategray;
  margin: 0 auto;
`;
export const CardItem = styled.div`
  // margin: 20px;
  padding: 20px;
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  justify-content: space-around;
  text-align: center;
`;
export const IndividualCard = styled.div`
  width: 200px;
  height: 320px;
  display: flex;
  margin: 0;
  padding: 0;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  box-shadow: 0 0 3px slategray;
  border: 1px solid #dbdbdb;
  border-radius: 8px;
  padding: 20px;
  transition: all 0.3s;
`;

export const ImageWrapper = styled.div`
  width: 150px;
  margin: 0 auto;
  object-fit: fit;
`;

export const ProductImage = styled.img`
  width: 100%;
`;
export const ParaTag = styled.p`
  margin: 0;
  padding: 3px;
  color: slategray;
`;
export const HeadingTag = styled.h3`
  color: black;
`;
