import CardItemDetails from "./CardItemDetails";
import React from "react";
import SearchDisplay from "./SearchDisplay";

import {
  Input,
  SearchSpace,
  SortSpace,
  FilterSpace,
  FilterDivison,
  CardItem,
  FeaturedBox,
} from "./styles";
import { sortSpace, filterSpace, productDetails } from "../../data/String";
import { FaSearch, FaSort, FaFilter } from "react-icons/fa";

export const Search: React.FC = () => {
  const [text, setText] = React.useState("");
  //   const [productList, setProductList] = React.useState<
  //     | {
  //         productId: number;
  //         productName: string;
  //         isFeatured: boolean;
  //         productBrand: string;
  //         productRating: number;
  //         productImage?: String;
  //         productPrice: Number;
  //       }[]
  //     | undefined
  //   >(CardItemDetails);
  //         productPrice: Number;
  const arr: {productId: number;  productImage?: String;productName: string; isFeatured: boolean; productRating: number;  productBrand: string;}[] =(CardItemDetails.filter(product=>product.productName.includes(text)));
   console.log(arr);
  return (
    <>
      <FilterDivison>
        <SearchSpace>
          <Input
            type="text"
            placeholder="Search Product"
            onChange={(e) => setText(e.target.value)}
          ></Input>
          <FaSearch />
        </SearchSpace>
        <SortSpace>
          {sortSpace.sortHeading}
          <FaSort />
        </SortSpace>
        <FilterSpace>
          {filterSpace.filterHeading}
          <FaFilter />
        </FilterSpace>
      </FilterDivison>
      <CardItem>
        {
       
        CardItemDetails.filter((product) =>
        product.productName.toLowerCase().includes(text.toLowerCase() )
        ).map((product) => {
           console.log(arr.length);
         
          return (
            <SearchDisplay
              id={product?.productId}
              name={product?.productName}
              brand={product?.productBrand}
              price={product?.productPrice}
              rating={product?.productRating}
              image={product?.productImage}
            />
          );
        
        })}
        {}
      </CardItem>
    </>
  );
};
