import styled from 'styled-components';

//Navigation content

export const Title = styled.div`

    display: flex;
    align-items: center;
    justify-content:space-around;
    padding:15px;
    color: white;
    font-size:20px;
    background-color:lightpink;
`
export const LogoImage = styled.img`
 
 width: 6vw;
 height:6vh;


`
export const AnchorTag = styled.a`
 
cursor:pointer;

&:hover {
    color:black;
    text-shadow:  0 0 3px slategray;
  }
`
//featured items content

export const Heading = styled.h3`
 
color:darkgrey;
font-size:25;

`
export const FeaturedBox = styled.div`
 
box-shadow:0 0 3px slategray;
margin:10px auto;
width:90%;
display:flex;
flex-direction:column;
justify-content:center;
align-items:center;

`

//search items content

export const FilterDivison = styled.div`
  
display:flex;
align-items:center;
justify-content:center;

`;

export const SearchSpace = styled.div`
  width: 20%;
  display: flex;
  justify-content: space-around;
  font-size: 18px;
  padding: 15px;
  margin: 10px;
  background: whitesmoke;
  color: lightpink;
  border-radius: 3px;
`;

export const Input = styled.input`
  
  background: whitesmoke;
 // width: 20%;
  padding:0;
  margin:0;
  color:lightpink;
  outline: none;
  border: none;
  font-size: 18px;
  ::placeholder {
    color: lightpink;
  }
`;

///sort
export const SortSpace = styled(SearchSpace)`
width:10%;
`;
export const FilterSpace =styled(SortSpace)`
`;
export const Button =styled(FilterSpace)`

/* :disabled {
    opacity: 0.4;
  } */
`;



//card items

export const CardContainer = styled.div`

  width:90%;
  box-shadow:0 0 3px slategray;
  margin:0 auto;

 
`;
export const CardItem = styled.div`

   // margin: 20px;
    padding: 20px;
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    justify-content: space-around;
    text-align: center;
 

 
`;
export const IndividualCard = styled.div`
width: 250px;
height:400px;
display:flex;
margin:0;
padding:0;
flex-direction:column;
box-shadow:0 0 3px slategray;
border: 1px solid #DBDBDB;
border-radius: 8px;
padding:  20px;
transition: all 0.3s;
`;

export const ImageWrapper = styled.div`
  width: 150px;

  margin:0;
  object-fit:fit;
`;
// export const Image = styled.div`

  
//   `;

export const ProductImage = styled.img`

 width: 100%;
`;
