import CardItemDetails from "./CardItemDetails";
import React from "react";
import SearchDisplay from "./SearchDisplay";

import {
  Input,
  SearchSpace,
  SortSpace,
  FilterSpace,
  FilterDivison,
  CardItem,
  Button,
} from "./styles";
import { sortSpace, filterSpace, productDetails } from "../../data/String";
import { FaSearch, FaSort, FaFilter } from "react-icons/fa";


export const Search: React.FC = () => {
    const [text,setText]=React.useState("");
//     const [productList, setProductList] = React.useState
//     <
//     | {
//         productId: number;
//         productName: string;
//         isFeatured: boolean;
//         productBrand: string;
//         productRating: number;
//         productImage?: String;
//         productPrice: Number;
//       }[]
//     | undefined
//   >
//   (CardItemDetails);
  console.log(CardItemDetails.filter(product=>product.productName.toLowerCase().includes("Ap")));
  return (
    <>
  
<SearchSpace>
            <Input
              type="text"
              placeholder="Search Product"
              onChange={(e) => 
                setText(e.target.value)
              }
            ></Input>
            <FaSearch />
          </SearchSpace>
          <SortSpace>
            {sortSpace.sortHeading}
            <FaSort />
          </SortSpace>
          <FilterSpace>
            {filterSpace.filterHeading}
            <FaFilter />
          </FilterSpace>
          <CardItem>
            {
                CardItemDetails.filter(product=>product.productName.toLowerCase().includes(text)).map((product) => {
                    
                    return (
                      <SearchDisplay
                        
                        id={product?.productId}
                        name={product?.productName}
                        brand={product?.productBrand}
                        price={product?.productPrice}
                        rating={product?.productRating}
                        image={product?.productImage}
                      />
                    );
                  })
            }
          </CardItem>
      
 
  </>
  );
};
