import { Input,SearchSpace,SortSpace,FilterSpace, FilterDivison,CardItem,Button} from "./styles";
import {sortSpace,filterSpace} from "../../data/String";
import { FaSearch,FaSort,FaFilter } from 'react-icons/fa';
import CardItemDetails from "./CardItemDetails";
import React from "react";
import SearchDisplay from "./SearchDisplay";

import { useState } from "react";
export const SearchItems:React.FC=()=> {
  

const [productList,setProductList]=React.useState<{productId:number,
    productName:string,
    isFeatured:boolean,
    productBrand:string,
    productRating:number,
    productImage?: String,
    productPrice:Number}[] | undefined>(CardItemDetails);
  const [text,setText]=React.useState<string>('');
  const handleOnClick=()=>
  {
    const findproducts=
    productList && productList?.length>0 ? productList?.filter((u)=>u?.productBrand===text)
    :undefined;
    setProductList(findproducts);
  };

    return (
   <>



<div>

<FilterDivison>
<SearchSpace>
<Input type="text"
placeholder="Search Product"
value={text}
onChange={(e)=>{setText(e.target.value);
setProductList(CardItemDetails);
}}></Input>
 <FaSearch/> 
</SearchSpace>
<SortSpace>
        {sortSpace.sortHeading}
         <FaSort/>
         
        </SortSpace>
        <FilterSpace>{filterSpace.filterHeading}
        <FaFilter/>
        </FilterSpace>
        </FilterDivison>
<Button disabled={!text} onClick={handleOnClick}>SEARCH</Button>
<CardItem>
    {
        productList && productList?.length===0 && (
            <div>Oops, No Product Found :(</div>
        )
    }
    {

        productList && productList?.length>0 && productList?.map(product =>{
            return(
<SearchDisplay
id={product?.productId}
name={product?.productName}
brand={product?.productBrand}
price={product?.productPrice}
rating={product?.productRating}
image={product?.productImage}

/>
       
            );
        })
    }
</CardItem>
</div>

        {/* <FilterDivison>
        <SearchSpace>    
        <Input type="text" placeholder="Search" ></Input>
        <FaSearch/> 
        </SearchSpace>
        <SortSpace>
        {sortSpace.sortHeading}
         <FaSort/>
         
        </SortSpace>
        <FilterSpace>{filterSpace.filterHeading}
        <FaFilter/>
        </FilterSpace>
        </FilterDivison> */}
       
        
   
        </>
       );
  }
