import styled from 'styled-components';

//Navigation content

export const Title = styled.div`

    display: flex;
    align-items: center;
    justify-content:space-around;
    padding:15px;
    color: white;
    font-size:20px;
    background-color:lightpink;
`
export const LogoImage = styled.img`
 
 width: 6vw;
 height:6vh;


`
export const AnchorTag = styled.a`
 
cursor:pointer;

&:hover {
    color:black;
    text-shadow:  0 0 3px slategray;
  }
`
//featured items content

export const Heading = styled.h3`
 
color:darkgrey;
font-size:25;

`
export const FeaturedBox = styled.div`
 
box-shadow:0 0 3px slategray;
margin:10px auto;
width:90%;
display:flex;
flex-direction:column;
justify-content:center;
align-items:center;

`

//search items content

export const Input = styled.input`
  font-size: 18px;
  /* padding: 10px;
  margin: 10px; */
  background: whitesmoke;
  color:lightpink;
   outline:none;
  border: none;
  /* border-radius: 3px; */
  ::placeholder {
    color: lightpink;
  }
`;
export const SearchSpace = styled.div`
 width:20%;
 display:flex;
 
  padding: 10px;
  margin: 10px;
  background: whitesmoke;
  color:lightpink;
   outline:none;
  border: none;
  border-radius: 3px;
  
`;
