import CardItemDetails from "./CardItemDetails";
import React from "react";
import SearchDisplay from "./SearchDisplay";

import {
  Input,
  SearchSpace,
  SortSpace,
  FilterSpace,
  FilterDivison,
  CardItem,
  Button,
} from "./styles";
import { sortSpace, filterSpace, productDetails } from "../../data/String";
import { FaSearch, FaSort, FaFilter } from "react-icons/fa";

export const SearchItems: React.FC = () => {
  const [productList, setProductList] = React.useState<
    | {
        productId: number;
        productName: string;
        isFeatured: boolean;
        productBrand: string;
        productRating: number;
        productImage?: String;
        productPrice: Number;
      }[]
    | undefined
  >(CardItemDetails);
  const [text, setText] = React.useState<string>("");
  const handleOnClick = () => {
    const findproducts =
      productList && productList?.length > 0
        ? productList?.filter((u) => u?.productBrand === text)
        : undefined;
    setProductList(findproducts);
  };

  return (
    <>
      <div>
        <FilterDivison>
          <SearchSpace>
            <Input
              type="text"
              placeholder="Search Product"
              value={text}
              onChange={(e) => {
                setText(e.target.value);
                setProductList(CardItemDetails);
              }}
            ></Input>
            <FaSearch />
          </SearchSpace>
          <Button disabled={!text} onClick={handleOnClick}>
            Search
          </Button>
          <SortSpace>
            {sortSpace.sortHeading}
            <FaSort />
          </SortSpace>
          <FilterSpace>
            {filterSpace.filterHeading}
            <FaFilter />
          </FilterSpace>
        </FilterDivison>

        <CardItem>
          {productList && productList?.length === 0 && (
            <div>{productDetails.message}</div>
          )}
          {productList &&
            productList?.length > 0 &&
            productList?.map((product) => {
              return (
                <SearchDisplay
                  id={product?.productId}
                  name={product?.productName}
                  brand={product?.productBrand}
                  price={product?.productPrice}
                  rating={product?.productRating}
                  image={product?.productImage}
                />
              );
            })}
        </CardItem>
      </div>
    </>
  );
};
